import React from 'react';
import './css/UsersManagement.css'; // Assuming you have a CSS file named UserManagement.css
import user_image1 from './images/user_image1.jpg'
import UserDetails from '../components/UserDetails';
import { useAuthContext } from '../hooks/useAuthContext';
import { useEffect,useState } from 'react';
import { useUserContext } from '../hooks/useUserContext';
import UserAddForm from '../components/UserAddForm';

const Users = () => {
    /*const [scripts, setScripts] = useState(null) we no longer need it we'll use context*/
    const {users,dispatch} = useUserContext();
    const {user} = useAuthContext()
    const [showModal, setShowModal] = useState(false);

    const toggleModal = () => {
      setShowModal(!showModal);
      console.log(showModal)
    };

    useEffect( ()=>
    {
      const fetchUsers = async ()=>{
        const response = await fetch('/api/user'/*, {
          headers: {'Authorization': `Bearer ${user.token}`},
        }*/)
        const json = await response.json()  //json type's is array of objects each object is a script we use .json() to transform a JSON-formatted array of JavaScript objects to array of Javascript objects      
        if(response.ok){ //if we get the data back successfully
          /*setScripts(json)   //update the state variable script with the fetched data (json). Updating the state triggers a re-render of the component, allowing you to display or work with the fetched data in your React component.
          console.log("ok")*/
          dispatch({type:'SET_USERS', payload:json})

        }
            
      }
      if (user) { //if we have a user we will fetch the scripts if not we will not fetch it
        fetchUsers()

  
      }
    },[  dispatch, user, users,showModal]) //we want it to be rendered only the first time 
    
    /*const CreateUser = async() => {

      if (!user) {
        setError('You must be logged in')
        return
      }
      const response = await fetch('/api/user', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          //'Authorization': `Bearer ${user.token}`
        },
        body: JSON.stringify({ title, load, reps }),
      });
      const json = await response.json()
      if (!response.ok){
        setError(json.error)
        setEmptyFields(json.emptyFields)
      }
      if(response.ok){
        
        setTitle('');
        setLoad('');
        setReps('');
        setError(null)
        setEmptyFields([])
        console.log('new script added',json)    
        dispatch({type: 'CREATE_SCRIPT', payload:json})
      }
      
    }*/

    /*const handleAddClick = () => {
      //setIsClickedSubmit(true);
      //CreateUser();
    };*/

/************************ */



/*********************** */

  return (
  <>
    <div className="user-management-container">
      <div className="header">
        <div className="column">Full Name</div>
        <div className="column">Username</div>
        <div className="column">Status</div>
        <div className="column">Role</div>
        <div className="actions">Actions</div>
      </div>
      <div className="user-list">
      {users === null ? ( // Render loading indicator while users are being fetched
        <p></p>
      ) : (users.map((user) => (
          <UserDetails user={user} key={user._id}/>)))
      }
      </div>

    </div>
    <div className="container-add">
      <button className="add-user-button"
      onClick={toggleModal}>Add new user</button>
      {showModal ? (
        <UserAddForm toggleModal={toggleModal}/>

      ) : (
        <div className="modal-hide">
          <div className="modal-show">
            <div className="modal-content">
              <h2>Modal Content</h2>
              <p>This is the content of the modal.</p>
              <button onClick={toggleModal}>Close</button>
            </div>
          </div>
        </div>

      )}
    </div>

    <div className={`password ${showPassword ? 'show' : ''}`}>
      <div className="monkey" onClick={toggleShowPassword}>
      <svg xmlns="http://www.w3.org/2000/svg" width="35" height="35" viewBox="0 0 64 64">
          <ellipse cx="53.7" cy="33" rx="8.3" ry="8.2" fill="#89664c" />
          <ellipse cx="53.7" cy="33" rx="5.4" ry="5.4" fill="#ffc5d3" />
          <ellipse cx="10.2" cy="33" rx="8.2" ry="8.2" fill="#89664c" />
          <ellipse cx="10.2" cy="33" rx="5.4" ry="5.4" fill="#ffc5d3" />
          <g fill="#89664c">
            <path d="m43.4 10.8c1.1-.6 1.9-.9 1.9-.9-3.2-1.1-6-1.8-8.5-2.1 1.3-1 2.1-1.3 2.1-1.3-20.4-2.9-30.1 9-30.1 19.5h46.4c-.7-7.4-4.8-12.4-11.8-15.2" />
            <path d="m55.3 27.6c0-9.7-10.4-17.6-23.3-17.6s-23.3 7.9-23.3 17.6c0 2.3.6 4.4 1.6 6.4-1 2-1.6 4.2-1.6 6.4 0 9.7 10.4 17.6 23.3 17.6s23.3-7.9 23.3-17.6c0-2.3-.6-4.4-1.6-6.4 1-2 1.6-4.2 1.6-6.4" />
          </g>
        </svg>
      </div>
      <div className="monkey-hands">
      <svg xmlns="http://www.w3.org/2000/svg" width="35" height="35" viewBox="0 0 64 64">
          <path fill="#89664C" d="M9.4,32.5L2.1,61.9H14c-1.6-7.7,4-21,4-21L9.4,32.5z" />
          <path fill="#FFD6BB" d="M15.8,24.8c0,0,4.9-4.5,9.5-3.9c2.3,0.3-7.1,7.6-7.1,7.6s9.7-8.2,11.7-5.6c1.8,2.3-8.9,9.8-8.9,9.8
          s10-8.1,9.6-4.6c-0.3,3.8-7.9,12.8-12.5,13.8C11.5,43.2,6.3,39,9.8,24.4C11.6,17,13.3,25.2,15.8,24.8" />
