import React, { useState, useEffect } from 'react';

const Documentation = ({ pages }) => {
  const [contentMap, setContentMap] = useState({});

  useEffect(() => {
    const fetchWikiPage = async (pageName) => {
      try {
        const response = await fetch(
          `https://raw.githubusercontent.com/wiki/azizeltaief/PFE/${pageName}.md`
        );
        if (response.ok) {
          const text = await response.text();
          setContentMap((prevContentMap) => ({
            ...prevContentMap,
            [pageName]: text,
          }));
        } else {
          throw new Error(`Failed to fetch wiki page: ${pageName}`);
        }
      } catch (error) {
        console.error('Error fetching wiki page:', error);
      }
    };

    pages.forEach((page) => fetchWikiPage(page));
  }, [pages]);

  return (
    <div>
      {/* Render the fetched wiki page content */}
      {Object.keys(contentMap).map((pageName) => (
        <div key={pageName}>
          <h2>{pageName}</h2>
          <div dangerouslySetInnerHTML={{ __html: contentMap[pageName] }} />
        </div>
      ))}
    </div>
  );
};

export default Documentation;
