import { useState } from "react";
import { useScriptContext } from "../hooks/useScriptContext";
import { useAuthContext } from '../hooks/useAuthContext'


const ScriptForm = () =>{
  const [email,setEmail] = useState('')
  const [password,setPassword] = useState('')
  const [usertype,setUsertype] = useState('')
  const [isClickedSubmit,setIsClickedSubmit] = useState(false)
  const [error,setError] = useState(null)
  const [emptyFields,setEmptyFields] = useState([])
  const {dispatch} = useScriptContext()
  const {user} = useAuthContext()

  const UserAddForm = async() => {

    if (!user) {
      setError('You must be logged in')
      return
    }
    const response = await fetch('/api/scripts', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${user.token}`
      },
      body: JSON.stringify({ title, load, reps }),
    });
    const json = await response.json()
    if (!response.ok){
      setError(json.error)
      setEmptyFields(json.emptyFields)
    }
    if(response.ok){
      
      setTitle('');
      setLoad('');
      setReps('');
      setError(null)
      setEmptyFields([])
      console.log('new script added',json)    
      dispatch({type: 'CREATE_SCRIPT', payload:json})
    }
    
  }

  const SaveUser = async() => {

    if (!user) {
      setError('You must be logged in')
      return
    }
    const response = await fetch('/api/user', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        //'Authorization': `Bearer ${user.token}`
      },
      body: JSON.stringify({ email, password, usertype }),
    });
    const json = await response.json()
    if (!response.ok){
      setError(json.error)
      setEmptyFields(json.emptyFields)
    }
    if(response.ok){
      
      setEmail('');
      setPassword('');
      setUsertype('');
      setError(null)
      setEmptyFields([])
      console.log('new script added',json)    
      dispatch({type: 'CREATE_USER', payload:json})
    }
    
  }




  const handleSubmitClick = () => {
    setIsClickedSubmit(true);
    SaveUser();
  };

  return(
    <>

      <div className="modal-show">
        <div className="modal-content">
          <label>User type</label>
          <input
            type="text"
            onChange={ (e) => setUsername(e.target.value)}
            value={usertype}
            className={emptyFields.includes('usertype')? 'error' : ''}     
          />

          <label>email</label>
          <input
            type="text"
            onChange={ (e) => setEmail(e.target.value)}
            value={email}  
            className={emptyFields.includes('email')? 'error' : ''}    
          />

          <label>password</label>
          <input
            type="text"
            onChange={ (e) => setPassword(e.target.value)}
            value={password}  
            className={emptyFields.includes('password')? 'error' : ''}    
          />

          <button 
          className="custom-button"
          type="submit"
          onClick={handleSubmitClick}>
            Submit
          </button>
          {error && <div className="error">{error}</div>}


          <button onClick={toggleModal}>Close Modal</button>
        </div>
      </div>






      




    
    </>
  );
}

export default UserAddForm
