import React from 'react';
import './css/Profile.css'; // Import CSS file for styling
import image1 from './images/image1.png'
const Profile = () => {
  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-image">
          <img src={image1} alt="Profile" />
        </div>

{/* Hidden file input */}
<input
          type="file"
          accept="image/*"
          ref={fileInputRef}
          style={{ display: 'none' }}
          onChange={handleFileChange}
        />
        {/* Upload button with label */}
        <label className="upload-button" onClick={handleUploadClick}>
          Upload Image
        </label>


        <button className="upload-button"  onClick={handleUploadClick}>Upload Image</button>
        <div className="input-group">
          <input type="text" placeholder="First Name" />
          <input type="text" placeholder="Last Name" />
        </div>
        <div className="input-group">
          <input type="email" placeholder="Email" />
          <input type="tel" placeholder="Phone" />
        </div>
        <button className="save-button">Save Profile</button>
      </div>
    </div>
  );
}

export default Profile;
