import React, { useRef, useState } from 'react';
import './css/Profile.css'; // Import CSS file for styling
import image1 from './images/image1.png'
import { useAuthContext } from "../hooks/useAuthContext"


const Profile = () => {

  /*Upload logic*/
  const fileInputRef = useRef(null);
  const handleUploadClick = () => {
    fileInputRef.current.click(); // Programmatically trigger file input click
  };
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    // Handle the file upload logic here, such as displaying the uploaded image
    console.log('Uploaded file:', file);
  };
  /*Upload logic*/

  const {user} = useAuthContext()
  const [email,setEmail] = useState(user.email)
  const [password,setPassword] = useState('')

  const UpdateUser = async() => {



    if (!user) {
      setError('You must be logged in')
      return
    }
    console.log(email+"*EMAIL*"+user.email)
    console.log(password+"*p*"+user.password)
    console.log(userType+"*t*"+user.userType)

    if (email === user.email  && password ===""  && userType === user.userType){
      console.log("No changes")
      setMessage("No changes to update")
      
      return
    }
    // Set email(the input) to user.email(the actual email before trying the modify process) if the input empty (we are not trying to modify a value)
    const updatedEmail = email === user.email ? user.email : email;
    const updatedPassword = password === '' ? user.password : password;

    console.log(updatedEmail)
    const response = await fetch('/api/user/'+user._id, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
        //'Authorization': `Bearer ${user.token}`
      },
      body: JSON.stringify({ email:updatedEmail, password:updatedPassword, userType }),
    });
    const json = await response.json()
    if (!response.ok){
      /*setError(json.error)
      setEmptyFields(json.emptyFields)*/
      setError(json.error)
    }
    if(response.ok){
      
      /*setEmail('');
      setPassword('');
      setUsertype('');*/
      setError(null)
      //setEmptyFields([])
      console.log('user modified',json)    
      dispatch({type: 'UPDATE_USER', payload:json})
      toggleModal1();
    }
  }


  const handleSubmitClick = () => {
    setIsClickedSubmit(true);
    UpdateUser();
  };





  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-image">
          <img src={image1} alt="Profile" />
        </div>

        {/* Hidden file input */  }
        <input
          type="file"
          accept="image/*"
          ref={fileInputRef}
          style={{ display: 'none' }}
          onChange={handleFileChange}
        />
        {/* Upload button with label */ }
        <label className="upload-button" onClick={handleUploadClick}>
          Upload Image
        </label>

        <div className="input-group">
          <input type="text" placeholder="First Name" />
          <input type="text" placeholder="Last Name" />
        </div>
        <div className="input-group">
          <input type="email" placeholder="Email" onChange={ (e) => setEmail(e.target.value)} value={email} />
          <input type="password" placeholder="Password" onChange={ (e) => setPassword(e.target.value)} value={password} />
        </div>

        {error && <div className="error">{error}</div>}   
        {message && <div className="message">{message}</div>} 


        <button className="save-button" type="submit" onClick={handleSubmitClick} >Save Profile</button>
      </div>
    </div>
  );
}

export default Profile;
